mapper.select:
		SELECT
		
		FROM sys_case sc, sys_project sp ,sys_model sm
		WHERE sp.id =sc.project_id AND sm.id=sc.model_id 
		AND sc.case_name like '%${caseName}%' 
		AND sc.project_id=#{projectId} 
		AND sc.status=#{status} 
		AND sc.model_id=#{modelId} 
		AND sc.api=#{api} 
		AND sc.version=#{version} 
        order by sc.id ASC
