mapper.select:
	SELECT 
	FROM sys_model sm LEFT JOIN sys_user su ON sm.test_id=su.id
	LEFT JOIN sys_user su2 ON sm.dev_id=su2.id LEFT JOIN sys_project sp ON
	sm.project_id =sp.id
	WHERE 1=1
	AND sm.model_name=#{modelName} 
	AND sm.project_id=#{projectId} 
	AND sm.status=#{status} 
	AND sm.test_id=#{testId} 
	AND sm.dev_id=#{devId} 
    order by sm.id ASC
